mapper.select:
        SELECT


--         级联表的字段
        ,daipiao.yonghu_id as daipiaoYonghuId
        ,daipiao.daipiao_name as daipiaoName
        ,daipiao.daipiao_photo as daipiaoPhoto
        ,daipiao.daipiao_address as daipiaoAddress
        ,daipiao.daipiao_lianxiren as daipiaoLianxiren
        ,daipiao.daipiao_lianxifangshi as daipiaoLianxifangshi
        ,daipiao.daipiao_types as daipiaoTypes
        ,daipiao.daipiao_new_money as daipiaoNewMoney
        ,daipiao.daipiao_time as daipiaoTime
        ,daipiao.daipiao_content as daipiaoContent
        ,daipiao.daipiao_zhuangtai_types as daipiaoZhuangtaiTypes
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.new_money as newMoney

        FROM daipiao_order  a
        left JOIN daipiao daipiao ON a.daipiao_id = daipiao.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and a.daipiao_order_uuid_number like CONCAT('%',#{params.daipiaoOrderUuidNumber},'%')


                and (
                    a.daipiao_id = #{params.daipiaoId}
                )


                and (
                    a.yonghu_id = #{params.yonghuId}
                    or daipiao.yonghu_id = #{params.yonghuId}
                )


                and daipiao.yonghu_id = #{params.yonghuId2}


                and
                    a.yonghu_id = #{params.yonghuId1}


                  and a.daipiao_order_true_price >= #{params.daipiaoOrderTruePriceStart}   


                  and a.daipiao_order_true_price <= #{params.daipiaoOrderTruePriceEnd}   


                and a.daipiao_order_types = #{params.daipiaoOrderTypes}


                and a.daipiao_order_payment_types = #{params.daipiaoOrderPaymentTypes}


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




                and a.daipiao_id IS NOT NULL


                and daipiao.daipiao_name like CONCAT('%',#{params.daipiaoName},'%')


                and daipiao.daipiao_address like CONCAT('%',#{params.daipiaoAddress},'%')


                and daipiao.daipiao_lianxiren like CONCAT('%',#{params.daipiaoLianxiren},'%')


                and daipiao.daipiao_lianxifangshi like CONCAT('%',#{params.daipiaoLianxifangshi},'%')


                and daipiao.daipiao_types = #{params.daipiaoTypes}



                  and daipiao.daipiao_new_money >= #{params.daipiaoNewMoneyStart}   


                  and daipiao.daipiao_new_money <= #{params.daipiaoNewMoneyEnd}   


                  and UNIX_TIMESTAMP(daipiao.daipiao_time) >= UNIX_TIMESTAMP(#{params.daipiaoTimeStart}) 


                  and UNIX_TIMESTAMP(daipiao.daipiao_time) <= UNIX_TIMESTAMP(#{params.daipiaoTimeEnd}) 


                and daipiao.daipiao_content like CONCAT('%',#{params.daipiaoContent},'%')


                and daipiao.daipiao_zhuangtai_types = #{params.daipiaoZhuangtaiTypes}



                  and UNIX_TIMESTAMP(daipiao.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(daipiao.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   


        order by a.${params.sort} ${params.order}
